function openPlace(){let a=document.querySelector(".header-place-head"),s=document.querySelector(".header-place-body");if(a&&s){let e=document.querySelector(".header-place-close"),t=document.querySelector(".header-place-city"),c=document.querySelectorAll(".sity-name");a.addEventListener("click",()=>{a.classList.contains("active")?(a.classList.remove("active"),s.classList.remove("active")):(a.classList.add("active"),s.classList.add("active"))}),e.addEventListener("click",()=>{a.classList.remove("active"),s.classList.remove("active")}),c.forEach(e=>{e.addEventListener("click",()=>{c.forEach(e=>{e.classList.contains("active")&&e.classList.remove("active")}),e.classList.add("active"),t.textContent=e.textContent,a.classList.remove("active"),s.classList.remove("active")})})}}openPlace();